function sendMessage() {
	if (messages.length == 0) {
		console.log("no more messages")
		return
	}
	let next = JSON.stringify(messages.shift())
	if (next == "null") {
		// silently skip
		return
	}
	console.log(`\nsending message:\n${next}\n`)
	connection.send(next)
}

